import React, {useState} from "react";
import { Link, useHistory } from "react-router-dom";
import {isAuthenticated} from "../../auth/index";
import "./style.css";
// api calls
import { signIn } from "../../auth/index";
// import images
import logo from "../../assets/images/newicons/logoBK.png";
export default function SignIn() {
  const history = useHistory();

  const [values, setValues] = useState({
    userName: "",
    password: "",
    error: "",
    success: false,
    loading: false,
    redirectToReferrer: false,
  });

  const {
    userName,
    password,
    error,
    loading,
    redirectToReferrer,
  } = values;
  const { user } = isAuthenticated();

  const handleInputs = (name) => (e) => {
    setValues({ ...values, [name]: e.target.value });
  };

  const clickSignIn = (e) => {
    e.preventDefault();
    setValues({ ...values, error: false, loading: true });
    signIn({ email, password }).then((data) => {
      if (data.error) {
        setValues({ ...values, error: data.error, loading: false });
      } else {
        authenticate(data, () => {
          setValues({ ...values, redirectToReferrer: true });
        });
      }
    });
  };
  const redirectUser = () => {
    if (redirectToReferrer) {
      if (user.role === 1) {
        return <Redirect to="/admin/dashboard" />;
      } else if (user.role === 0) {
        return <Redirect to={`/auth/${user._id}`} />;
      }
    }
    if (isAuthenticated()) {
      return <Redirect to={`/auth/community/${user._id}`} />;
    }
  };
  const showError = () => (
    <div className=" alert-danger" style={{ display: error ? "" : "none" }}>
      {error}
    </div>
  );
  const showLoading = () =>
    loading && (
      <div className="success-message">
        <p>Loading ...</p>
      </div>
    );
  const FormComponent = () => (
    <div className="user-sign-up-in-cont">
      <div className="comapany-welcome">
        <Link to="/">
          <img src={logo} alt="" />
        </Link>
        {showError()}
        {showLoading()}
        <h3>Welcome!</h3>
        <p style={{ marginTop: "15px" }}>Please sign in</p>
      </div>
      <form className="user-sign-in" onSubmit={clickSignIn}>
        <input
          value={userName}
          type="text"
          onChange={handleInputs("userName")}
          placeholder="Username"
        />
        {/* might need to add "passwords do not match" */}
        <input
          value={password}
          type="password"
          onChange={handleInputs("password")}
          placeholder="Password"
        />
        <button>Sign In</button>
      </form>
      <Link id="to-signin" to="/signup">
        Need to sign up?
      </Link>
      <br />
      <Link id="forgot-pass" to="/forgotpass">
        Forgot Password?
      </Link>
    </div>
  );
  return (
    <div>
      {FormComponent()}
      {redirectUser()}
    </div>
  );
}
