  const formComp = () => {
    return (
      <form onSubmit={formSubmit()} className="cp-f-cont">
        <input type="file" onChange={handleChange("img")} accept=".jpg,.jpeg,.png" />
        <textarea typeof="text" onChange={handleChange("postText")} maxLength="150"></textarea>
        <button typeof="submit">Post</button>
      </form>
