import React, { useState, useEffect } from "react";

export default function CreatePostModal({ userPostsId, token }) {
  const data = useState({
    img: "",
    postText: "",
    userId: userPostsId,
    formData: "",
    loading: false,
    error: "",
  });
  const { img, postText, user, userId, formData, loading, error } = data;

  const formSubmit = () => {
    console.log(postText, userId);
  };

  const formComp = () => {
    return (
      <form onSubmit={formSubmit}>
        <input type="file" accept=".jpg,.jpeg,.png" />
        <textarea typeof="text" maxLength="150"></textarea>
        <button>Post</button>
      </form>
    );
  };

  return <div className="cp-modal-overlay">{formComp()}</div>;
}
