import React, { useState, useEffect } from 'react';
import Header from "../../Components/Header";
import Footer from "../../Components/Footer";
import axios from "axios";
// import components
import JokeCard from "../../Components/JokeCard";
import "./style.css";


// ajax call to save saved joke to database
// get save button to work


export default function Jokes() {
    var jokesTitle = "Dad Jokes";
    const [jokeArr, setJokeArr] = useState([]);
    // const jokeArr = [];

    useEffect(() => {
        jokeSearch()
        // console.log(jokeArr)

    })

    // joke api call
    function jokeSearch() {
        // for some reason the slack api link works best
        var queryURL = "https://icanhazdadjoke.com/slack";
        var jokeInterval = setInterval(() => {
            for (var i = 0; i < 10; i++) {
                axios({
                    url: queryURL,
                    method: "GET"
                }).then(res => {
                    // setJokeArr(res.data.attachments[0].text)
                    console.log(res.data.attachments[0].text)
                });
            }
        }, 1000);

    };

    return (
        <div className="joke-body">
            <Header
                pageTitle={jokesTitle.toUpperCase()}
            />
            {/* {jokeArr.map(res => (
                <JokeCard
                    joke={res.text}
                />
            ))} */}
            <Footer />
        </div>
    )
}
