import React, {useState, useEffect} from 'react';
import Header from "../../Components/Header";
import Footer from "../../Components/Footer";
import axios from "axios";
// import components
import JokeCard from "../../Components/JokeCard";
import "./style.css";


// ajax call to save saved joke to database
// get save button to work
export default function Jokes() {
    var jokesTitle = "Dad Jokes";

    const [joke, setJoke] = useState([]);
    // joke api call
    function jokeSearch() {
        // for some reason the slack api link works best
        var queryURL = "https://icanhazdadjoke.com/slack"; 
        for(var i = 0; i < 10; i++){
            axios({
                url: queryURL,
                method: "GET"
            }).then(function (res) {
                console.log("got the jokes");
                console.log(res.data.attachments[0].text);
                setJoke(res.data.attachments[0].text)
            });
        }
    };
    useEffect(() => {
        jokeSearch();
    }, [])

    // end joke api
    return (
        <div className="joke-body">
            <Header
            pageTitle = {jokesTitle.toUpperCase()}
            />
            {/* content here */}
            {joke.map(res=>(
                <JokeCard
                joke = {res}
                />
            ))}

            <Footer/>
        </div>
    )
}
